import java.util.regex.Matcher;
import java.util.regex.Pattern;
public class RegexReplace {
	public static void main(String[] args) {
		// Sample input string
		String inputString = "Hello, <name>! Today is <day>.";
		// Define a regular expression pattern
		String regex = "<(.*?)>";
		// Create a Pattern object
		Pattern pattern = Pattern.compile(regex);
		// Create a Matcher object
		Matcher matcher = pattern.matcher(inputString);
		// Replace matches with desired values
		String result = matcher.replaceAll(matchResult -> {
			// Here, you can customize the replacement logic based on match groups
			switch (matchResult.group(1)) {
				case "name":
					return "John";
				case "day":
					return "Monday";
				default:
					return "Unknown";
			}
		});
		// Print the result
		System.out.println("Original: " + inputString);
		System.out.println("Modified: " + result);
	}
}

